function addTogether(...arr) {

  if(arr.length === 2){
    if(typeof arr[0] === 'number' && typeof arr[1] === 'number'){
      return arr[0] + arr[1];
    }

  }
  else if(typeof arr[0] === 'number'){
    return function (el){
      if(typeof arr[0] === 'number' && typeof el === 'number'){
        console.log(el);
        return arr[0] + el;
      }
    }
  }


  return undefined;
}


