function rot13(str) { // LBH QVQ VG!
  var arr = str.split('');
  for(let i = 0; i<arr.length; i++){
      if(/[A-Z]/.test(arr[i])){
           var temp = arr[i].charCodeAt(0) + 13;
           if(temp > 90){
               temp = 64 + (temp - 90);
           }
           arr[i] = String.fromCharCode(temp);
      }

  }
  return arr.join('');
}

