function orbitalPeriod(arr) {
  var GM = 398600.4418;
  var earthRadius = 6367.4447;
  var nArr = [...arr];
    for(var item in nArr){
      // change avgAlt to orbitalPeriod
      console.log(item.avgAlt);
        var avg = arr[item].avgAlt;

        var orb = Math.round(2 * Math.PI * Math.sqrt(
          Math.pow(earthRadius + avg,3) / GM
        )); 


        delete arr[item].avgAlt;
        arr[item].orbitalPeriod = orb;

    }

  return arr;
}

