function palindrome(str) {
  // Good luck!

  var arr = str.toLowerCase().replace(/[^a-zA-Z0-9]+/g,'').split('');
  console.log(arr);
  for(let i = 0; i < arr.length; i++){
    if(arr[i] !== arr[arr.length-1-i]){
      return false;

    }
  }

  return true;
}

