function convertToRoman(num) {
    var vals = [1,4,5,9,10,40,50,90,100,400,500,900,1000];
    var roman = ['I', 'IV', 'V', 'IX', 'X', 'XL','L', 'XC', 'C','CD','D','CM', 'M'];
// ones

var index = vals.length - 1;
var numeral = "";

while(num > 0){
  if(vals[index] <= num ){
    num -= vals[index];
    numeral+= roman[index];
  }
  else{

    index--;
  }

}


console.log(numeral);
 return numeral;
}

convertToRoman(36);
