  arr.sort((a,b) => b-a);

  var expand = [] ;
  for(let i = arr[0] ; i >= arr[1];i--){
    expand.push(i);
  }

  var com = expand[0] * expand[1];
  var loop = 1;
  var gotCom = false;
  console.log(com);

  do{

    com = expand[0] * expand[1] * loop;
    gotCom = true;
    for(let i = 0; i < expand.length; i++){
      if(com % expand[i] !== 0){
        gotCom = false;
      }
    }


  loop++;
  }while (!gotCom);

console.log(com);
  return com;
}


