function steamrollArray(arr) {
  // I'm a steamroller, baby
var nArr = [];
  for(let i = 0; i < arr.length; i++){

    if(Array.isArray( arr[i] )){
      nArr = nArr.concat(...steamrollArray(arr[i]));
    }
    else{
      nArr.push(arr[i]);
    }
  }
  console.log(nArr);

  return nArr;
}

