// define ADD, addMessage(), messageReducer(), and store here:

const ADD = 'ADD';
const defaultstate = []
function addMessage(message){
    return {
        type:ADD,
        message
    }
}

function messageReducer(state = defaultstate, action){
    switch (action.type){
        case ADD:
            return ([...state,action.message]);
            break;
        default:
            return state;
    }
}

const store = Redux.createStore(messageReducer);
